    private void validacionDatosInsertados() {
        String nombre = ETnombre.getEditText().getText().toString().toUpperCase().toUpperCase();
        String domicilio = ETdomicilio.getEditText().getText().toString().toUpperCase().toUpperCase();
        String claveelectoral = ETclaveelectoral.getEditText().getText().toString().toUpperCase().toUpperCase();
        String curp = ETcurp.getEditText().getText().toString().toUpperCase().toUpperCase();
        String anioregistro = ETanioregistro.getEditText().getText().toString().toUpperCase().toUpperCase();
        String numeroversion = ETnumeroversion.getEditText().getText().toString().toUpperCase().toUpperCase();
        String localidad = ETlocalidad.getEditText().getText().toString().toUpperCase().toUpperCase();
        String anioemision = ETanioemision.getEditText().getText().toString().toUpperCase().toUpperCase();
        String vigencia = ETvigencia.getEditText().getText().toString().toUpperCase().toUpperCase();

        if (nombre.equals("")){
            ETnombre.setError("Nombre requerido");
        } else if (domicilio.equals("")){
            ETdomicilio.setError("Domicilio requerido");
        } else if (claveelectoral.equals("")){
            ETclaveelectoral.setError("Clave Electoral requerido");
        } else if (curp.equals("")){
            ETcurp.setError("Curp requerido");
        } else if (anioregistro.equals("")){
            ETanioregistro.setError("Ao de Registro requerido");
        } else if (numeroversion.equals("")){
            ETnumeroversion.setError("Numero de Version requerido");
        } else if (localidad.equals("")){
            ETlocalidad.setError("Localidad requerido");
        } else if (anioemision.equals("")){
            ETanioemision.setError("Ao de Emision requerido");
        } else if (vigencia.equals("")){
            ETvigencia.setError("Vigencia requerido");
        }
    }
